ar,center_code,district,original_budget,current_budget)select f_year,center_code,district,'',sum(requested_amount)from equipment_request_3where 1 AND status='active'and division_approved='y'and equipment_description != 'budget'and f_year='$f_year'group by center_code;";   $result = @mysqli_query($connection, $query,$connection);if($showSQL){echo "$query<br><br>";}; $query = "select f_year,center_code,district,sum(original_budget) as 'original_budget',sum(current_budget) as 'current_budget',sum(original_budget-current_budget) as 'original_current'from equipment_center_totalswhere 1group by center_code;";   $result = @mysqli_query($connection, $query,$connection);if($showSQL){echo "$query<br><br>";};$header="<tr><th>f_year</th><th>center_code</th><th>district</th><th>original_budget</th><th>current_budget</th><th>original-current</th></tr>";echo "$header";$j=1;if($rep=="excel"){$forceText="'";}while($row = mysqli_fetch_array($result)){extract($row);$original_budgetTOT+=$original_budget;$current_budgetTOT+=$current_budget;$original_currentTOT+=$original_current;$original_budget=number_format($original_budget,2);$current_budget=number_format($current_budget,2);$original_current=number_format($original_current,2);if($rep==""){if(fmod($j,20)==0){echo "$header";}$j++;}if($original_current<0){$f1="<font color='red'>";$f2="</font>";$tr=" bgcolor='AliceBlue'";}else{$f1="";$f2="";$tr="";}echo "<tr$tr>";echo "<td align='center'>$forceText$f_year</td><td align='center'>$center_code</td><td align='center'>$district</td><td align='right'>$original_budget</td><td align='right'>$current_budget</td>";echo "<td align='right'>$f1$original_current$f2</td></tr>";	}// end while$original_budgetTOT=number_format($original_budgetTOT,2);$current_budgetTOT=number_format($current_budgetTOT,2);$original_currentTOT=number_format($original_currentTOT,2);if($original_currentTOT<0){$f1="<font color='red'>";$f2="</font>";}else{$f1="";$f2="";}