_error());echo "<tr><td colspan='10'>$sql</td></tr>";echo "<tr><th colspan='10'>Record(s) from <font color='red' size='+1'>pcard_unreconciled</font></th></tr><tr><th>Select</th><th>center</th><th>center_desc</th><th>vendorname</th><th>invoice</th><th>amount</th><th>trans_date</th><th>project_number</th></tr>";while($row=mysqli_fetch_array($result)){extract($row);if($passAmount==$amount){$f1="<font color='green'>";$f2="</font>";}else{$f1="";$f2="";}if($passInvoice==$invoice){$f3="<font color='green'>";$f4="</font>";}else{$f3="";$f4="";}$sel="<input type='radio' name='passProj_num' value='$project_number' checked>";echo "<tr><td>$sel</td><td>$center</td><td>$center_desc</td><td></td><td>$vendorname</td><td>$f1$amount$f2</td><td>$f3$trans_date$f4</td><td align='center'><font color='red'>$project_number</font></td></tr>";	}// end while	echo "	<tr><td align='center' colspan='3'>	<input type='hidden' name='start' value='$start'>	<input type='hidden' name='end' value='$end'>	<input type='hidden' name='fy' value='$fy'>	<input type='submit' name='submit' value='Update'></td><td colspan='8'>Select the records from <font color='green'>partf_payments</font> to update WITH the project_number from <font color='green'>pcard_unreconciled</font></td></tr>	</table>";}else{$today_60=date('Ymd', mktime(0, 0, 0, date("m"), date("d")-60, date("Y")));$passAmount=$amount;$passInvoice=$invoice;$ci=$center."-".$invoice;$ca=$center."-".$amount;$sql = "select vendor_name as 'vendorname',ncas_invoice_number as 'invoice',ncas_invoice_amount as 'amount',datesql as 'invoice_date',project_number,idfrom cid_vendor_invoice_paymentswhere 1and (ci='$ci' or ca='$ca')and datesql >='$today_60'group by id";$result = @mysqli_query($connection, $sql) or die("$sql Error 1#". mysql_errno() . ": " . mysqli_error());$num=mysqli_num_rows($result);echo "<tr><th colspan='10'>Record(s) from <font color='red' size='+1'>cid_vendor_invoice_payments</font></th></tr>";//echo "<tr><td colspan='10'>$sql</td></tr>";echo "<tr><th>Select</th><th>center</th><th>center_desc</th><th>vendorname</th><th>invoice</th><th>amount</th><th>invoice_date</th><th>project_number</th></tr>";while($row=mysqli_fetch_array($result)){extract($row);if($passAmount==$amount){$f1="<font color='green'>";$f2="</font>";}else{$f1="";$f2="";}if($passInvoice==$invoice){$f3="<font color='green'>";$f4="</font>";}else{$f3="";$f4="";}$sel="<input type='radio' name='passProj_num' value='$project_number' checked>";echo "<tr><td>$sel</td><td>$center</td><td>$center_desc</td><td>$vendorname</td><td>$f3$invoice$f4</td><td align='right'>$f1$amount$f2</td><td>$invoice_date</td><td align='center'><font color='red'>$project_number</font></td></tr>";	}// end whileif($num>0){echo "	<tr><td align='center' colspan='3'>	<input type='hidden' name='start' value='$start'>	<input type='hidden' name='end' value='$end'>	<input type='hidden' name='fy' value='$fy'>	<input type='submit' name='submit' value='Update'></td><td colspan='8'>Select the records from <font color='blue'>partf_payments</font> to update WITH the project_number from <font color='red'>cid_vendor_invoice_payments</font></td></tr>";}echo "</table>";}echo "</form></body></html>";?>