<?php//These are placed outside of the webserver directory for securityinclude("../../../include/authBUDGET.inc"); // used to authenticate users$database="budget";$db="budget";include("/opt/library/prd/WebServer/include/iConnect.inc"); // connection parametersmysqli_select_db($connection, $database); // databaseinclude("../../../include/activity.php");//echo "<pre>";print_r($_REQUEST);echo "</pre>";exit;extract($_REQUEST);//include_once("../menu.php");if($rep=="excel"){header('Content-Type: application/vnd.ms-excel');header('Content-Disposition: attachment; filename=DPR_Contract_Balances.xls');}$WHERE = "WHERE 1  AND contract_num =  '$contract_num'";$sql="SELECT proj_num as 'DPR_projnum', partf_projects.park as 'Park', partf_projects.projname as 'DPR_Projname', contract_num as 'DPR_Contractnum', vendorname as 'Contractor', partf_payments.center, partf_payments.account, coa.park_acct_desc as 'account_description',contract_amt,noncon_amt,amount as 'invoice_amt',datenew,invoiceFROM `partf_payments`left join coa on partf_payments.account=coa.ncasnumLEFT JOIN partf_projects on partf_projects.projNum=partf_payments.proj_num$WHEREORDER BY `datenew` desc, center";if($level>4 and $rep==""){echo "$sql";}//exit;$result = mysqli_query($connection, $sql) or die ("Couldn't execute query. $sql");$num=mysqli_num_rows($result);echo "<div align='center'><font color='green'>DPR_Contract_Payments</font> for Contract Number $contract_num &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<A HREF=\"javascript:window.print()\">Click to Print This Page</A>";echo "<table border='1'>";/*echo "<tr><td><a href='DPR_Contract_Balances.php?$varQuery&rep=excel'>Excel Export</a></td><tr>";*/$titleArray=array("DPR_projnum","park","DPR_Projname","DPR_Contractnum","Contractor","center", "account", "account_description", "contract_amt","noncon_amt","invoice_amt","invoice","datepost");for($i=0;$i<count($titleArray);$i++){$t=strtoupper(str_replace("_","<br>",$titleArray[$i]));echo "<th>$t</th>";}while ($row=mysqli_fetch_array($result)){extract($row);$totContract += $contract_amt;$totNonContract += $noncon_amt;$totInvoice += $invoice_amt;$contract_amt=number_format($contract_amt,2);$noncon_amt=number_format($noncon_amt,2);$invoice_amt=number_format($invoice_amt,2);echo "<tr><td align='center'>$DPR_projnum</td><td align='center'>$Park</td><td>$DPR_Projname</td><td align='center'>$DPR_Contractnum</td><td>$Contractor</td><td align='center'>$center</td><td align='center'>$account</td><td align='center'>$account_description</td><td align='right'>$contract_amt</td><td align='right'>$noncon_amt</td><td align='right'>$invoice_amt</td><td align='center'>$invoice</td><td align='center'>$datenew</td></tr>";}$totContract=number_format($totContract,2);$totNonContract=number_format($totNonContract,2);$totInvoice=number_format($totInvoice,2);echo "<tr><td colspan='9' align='right'><b>$totContract</b></td><td align='right'><b>$totNonContract</b></td><td align='right'><b>$totInvoice</b></td><td colspan='2'></td></tr>";echo "</table></div></body></html>";?>