<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction showHeader(){echo "<table border='1'><tr><th>Budget Code</th><th>Fund</th><th>Fund Name</th><th>Non-State Approp. Revs.</th><th>State Approp. Revs.</th><th>Total Revs.</th><th>Total Expenditures</th><th>Posted End Balance</th><th>Valid Record</th><th>Post Thru Date</th></tr>";}function recordShow($bud_code,$fund,$fund_name,$non_sa_revs,$sa_revs,$total_revs,$total_expend,$posted_end_bal,$valid_record,$post_thru_date,$datefind,$cipid){$non_sa_revs=formatMoney($non_sa_revs);$sa_revs=formatMoney($sa_revs);$total_revs=formatMoney($total_revs);$total_expend=formatMoney($total_expend);$posted_end_bal=formatMoney($posted_end_bal);echo "<tr><td>$bud_code</td><td>$fund</td><td>$fund_name</td><td align='right'>$non_sa_revs</td><td align='right'>$sa_revs</td><td align='right'>$total_revs</td><td align='right'>$total_expend</td><td align='right'>$posted_end_bal</td><td>$valid_record</td><td>$post_thru_date</td></tr>";}function formatMoney($n){$val=number_format($n,2);return $val;}?>