<?php// *************** Show Contract **************// Headerecho "<table border='1' cellpadding='3'><tr><th>Contract #</th><th>Proj. #</th><th>Con-Pro #</th><th>Park/Description</th><th>PO #</th><th>Contractor</th><th>Trans. Type</th><th>Change Order #</th><th>Amount</th><th>Fully Approv.</th><th>Comment</th></tr>";// Display Functionfunction permitShow0($dpr_contract_num,$dpr_proj_num,$con_pro_num,$contractor,$trans_type,$change_order_num, $entry_date,$amount, $fully_approv,$comments,$po_num,$ctid,$park,$projNum){global $contract,$check0,$check1;$contractor=strtoupper($contractor);$amount=number_format($amount,2);if($dpr_contract_num==$check0){$link0="\"";}else{$check1="";$link0="<a href='contract.php?contract=$contract&varEdit=conTran&dpr_contract_num=$dpr_contract_num&e=1'>$dpr_contract_num</a>";}echo "<tr><td align='center'>$link0</td>";if($dpr_proj_num==$check1){$link1="\"";}else{$link1="$dpr_proj_num";}echo "<td align='center'>$link1</td><td>$dpr_contract_num - $dpr_proj_num</td><td>$park - $projNum</td><td>$po_num</td><td>$contractor</td><td>$trans_type</td><td align='center'>$change_order_num</td><td align='right'>$amount</td>";echo "<td align='center'>$fully_approv</td><td>$comments</td></tr>";$check0=$dpr_contract_num;$check1=$dpr_proj_num;}// Display Total functionfunction showTotal($totAmt){$totAmt=number_format($totAmt,2);echo "<tr><td colspan='9' align='right'>$totAmt</td></tr>";};?>