<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($invoice,$pe,$description,$debit,$credit,$sys,$acct,$center,$fund,$acctdate,$whid){$link="<a href='exp_rev_edit.php?whid=$whid'>$whid</a>";$credit=formatMoney($credit);$debit=formatMoney($debit);echo "<tr><td align='center'>$link</td><td align='left'>$center</td><td align='left'>$fund</td><td align='center'>$acct</td><td align='center'>$invoice</td><td align='center'>$description</td><td align='center'>$debit</td><td align='center'>$credit</td><td align='center'>$sys</td><td align='center'>$pe</td><td align='center'>$acctdate</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>