<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($center,$acctdate,$invoice,$description,$deb,$cre,$acct){$ba=formatMoney($deb);$pp=formatMoney($cre);$link="<a href='transAll.php?center=$center&acct=$acct'>$acct</a>";echo "<tr><td>$center</td><td align='center'>$link</td><td align='right'>$description</td><td align='right'><font color='red'>$ba</font></td><td align='right'>$pp</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>