<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($rcc,$park,$expenseType,$budAllocate,$payPosted,$warehouse,$endBalance){$ba=formatMoney($budAllocate);$pp=formatMoney($payPosted);$wa=formatMoney($warehouse);$eb=formatMoney($endBalance);if($eb<0){$c="red";}else{$c='black';}echo "<tr><td>$rcc</td><td align='center'>$park</td><td align='center'>$expenseType</td><td align='right'>$ba</td><td align='right'>$pp</td><td align='right'>$wa</td><td align='right'><font color='$c'>$eb</font></td></tr>";// <td>$parkName</td>}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>