<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($card,$lastname,$vendor,$postdate,$transid,$transdate,$company,$acct,$center,$amt,$descrip,$payst,$pcardid){$link="<a href='pcard_edit.php?pcardid=$pcardid'>$card</a>";echo "<tr><td align='center'>$link</td><td align='left'>$lastname</td><td align='left'>$vendor</td><td align='center'>$postdate</td><td align='center'>$transid</td><td align='center'>$transdate</td><td align='center'>$company</td><td align='center'>$acct</td><td align='center'>$center</td><td align='center'>$amt</td><td align='center'>$descrip</td><td align='center'>$payst</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>