<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($rcc,$expenseType,$ncasacct,$effdate,$charges,$credits,$transtype,$invoiceNum,$vendor){$ba=formatMoney($charges);$pp=formatMoney($credits);if($ba<0){$c="red";}else{$c='black';}if($pp<0){$c="red";}else{$c='black';}//$link="<a href='trans.php?rcc=$rcc&expenseType=$expenseType'>$expenseType</a>";echo "<tr><td>$rcc</td><td align='center'>$expenseType</td><td align='center'>$ncasacct</td><td align='center'>$effdate</td><td align='right'><font color='$c'>$ba</font></td><td align='right'><font color='$c'>$pp</font></td><td align='center'>$transtype</td><td align='center'>$invoiceNum</td><td align='left'>$vendor</td></tr>";// <td>$parkName</td>}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>