<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($center,$acct,$acctdate,$invoice,$pe,$description,$debit,$credit,$sys){$ba=formatMoney($debit);$pp=formatMoney($credit);if($ba<0){$c="red";}else{$c='black';}if($pp<0){$c="red";}else{$c='black';}//$link="<a href='trans.php?rcc=$rcc&expenseType=$expenseType'>$expenseType</a>";echo "<tr><td>$center</td><td align='center'>$acct</td><td align='center'>$acctdate</td><td align='center'>$invoice</td><td align='center'>$pe</td><td align='center'>$description</td><td align='right'><font color='$c'>$ba</font></td><td align='right'><font color='$c'>$pp</font></td><td align='center'>$sys</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>