<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($center,$acct,$deb,$cre){$ba=formatMoney($deb);$pp=formatMoney($cre);if($ba<0){$c="red";}else{$c='black';}if($pp<0){$c="red";}else{$c='black';}$link="<a href='transAll.php?center=$center&acct=$acct'>$acct</a>";echo "<tr><td>$center</td><td align='center'>$link</td><td align='right'><font color='$c'>$ba</font></td><td align='right'><font color='$c'>$pp</font></td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>