<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($company,$fund,$center,$account,$datePost,$checknum,$invoice,$amount,$vendornum,$groupnum,$vendorname,$proj_num,$contract_num,$conpro_num,$contract_amt,$noncon_amt,$sec_proj_req,$sec_proj_num,$charg_proj_num,$dateInvoice,$datenew,$xtid,$projPark){global $accessLevel;$d=strftime("%Y%m%d",strtotime($datePost));$na=$amount - $contract_amt;$AM=formatMoney($amount);$CA=formatMoney($contract_amt);$NA=formatMoney($na);if($AM<0){$a="red";}else{$a='black';}if($NA<0){$n="red";}else{$n='black';}if($accessLevel=="2"){$linkXtid="<a href='partf_payments_edit.php?xtid=$xtid'>$xtid</a>";}else{$linkXtid=$xtid;}$linkFund="$fund <a href='partf_payments.php?fund=$fund'><img src='Tdown.png'</a>";$linkAcct="$account <a href='partf_payments.php?acct=$account'><img src='Tdown.png'</a>";$linkProj="$proj_num <a href='partf_payments.php?proj_num=$proj_num'><img src='Tdown.png'</a>";$linkVenNum="$vendornum <a href='partf_payments.php?vendornum=$vendornum'><img src='Tdown.png'</a>";echo "<tr><td>$linkXtid</td><td>$company</td><td>$linkFund</td><td align='center'>$center</td><td align='center'>$linkAcct</td><td align='center'>$datenew</td><td align='center'>$checknum</td><td align='center'>$invoice</td><td align='right'><font color='$a'>$AM</font></td><td align='center'>$linkVenNum</td><td align='center'>$groupnum</td><td align='center'>$vendorname</td><td align='center'>$linkProj</td><td align='center'>$contract_num</td><td align='center'>$conpro_num</td><td align='right'><font color='$c'>$CA</font></td><td align='right'><font color='$n'>$NA</font></td><td align='center'>$sec_proj_req</td><td align='center'>$sec_proj_num</td><td align='center'>$charg_proj_num</td><td align='center'>$misc</td><td align='center'>$projPark</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>