<?php// *************** Show Project FUNCTIONS **************// All Projectsfunction itemShow($center,$acctdate,$invoice,$description,$deb,$cre,$acct,$acctSeries,$bud){$bal=($cre-$deb)+$bud;$de=formatMoney($deb);$cr=formatMoney($cre);$ba=formatMoney($bal);if($ba>0){$f="";$f1="";}else{$f="<font color='red'>";$f1="</font?";}$link="<a href='transLine.php?center=$center&acct=$acctSeries'>$acctSeries</a>";echo "<tr><td>$center</td><td align='center'>$link</td><td align='right'>$bud</td><td align='right'><font color='red'>$de</font></td><td align='right'>$cr</td><td align='right'>$f$ba$f1</td></tr>";}function formatMoney($fm){$value=number_format($fm,2);return $value;}?>