<?php// *************** PMIS FUNCTIONS **************function portalHeader($passSQL,$colHeaders){global $numOfColumns;$fld=explode(",",$colHeaders);// Put Field Names in an Array$c=count($fld)-1;$lastFld=$fld[$c];$numOfColumns=count($colHeaders);echo "<table border='1' cellpadding='3'><tr>";for($x=0;$x<$numOfColumns;$x++){$var=strtoupper($colHeaders[$x]);echo "<th>$var</th>";}echo "</tr>";}function itemShow($row3,$linkVars){global $granTotal,$numOfColumns,$reportFlds,$spent_1_array,$spent_2_array;//echo "<pre>";print_r($spent_1_array);echo "</pre>";//exit;$fld2=$linkVars[2];if($fld2){$linkDateRange="&like%5B10%5D=2&acctdate=$fld2";}echo "<tr>";list($parkcode,$center,$acct,$park_acct_desc,$spent_3,$park_req,$park_just)=$row3;echo "<td>$parkcode</td><td>$center</td>";echo "<td>$acct</td><td>$park_acct_desc</td>";$total1=-$spent_1_array[$acct];$total1=number_format($total1,2);$total2=-$spent_2_array[$acct];$total2=number_format($total2,2);echo "<td align='right'>$total1</td><td align='right'>$total2</td>";$granTotal=$granTotal+$spent_3;$total3=-$spent_3;$ra=$total3+$park_req;$total3=number_format($total3,2);$ra=number_format($ra,2);$park_req=number_format($park_req,2);/*$varURL="<a href='portal.php?dbTable=inc_dec&center=$center&ncas_acct=$acct' target='_blank'>$park_req</a>";*/$varURL="TBA";echo "<td align='right'>$total3</td><td align='right'>$varURL</td>";echo "<td align='right'>$ra</td><td>$park_just</td>";echo "</tr>";}?>