<?php// *************** Show Project FUNCTIONS **************// All Projects	function permitShow0($projNum,$projYN,$reportDisplay,$projCat,$projSCnum,$projDENRnum,$Center,$budgCode,$comp,$projsup,$manager,$fundMan,$YearFundC,$YearFundF,$fullname,$dist,$county,$section,$park,$projName,$active,$startDate,$endDate,$statusProj,$percentCom,$statusPer,$comments,$commentsI,$dateadded,$brucefy,$proj_man,$secondCounty,$div_app_amt,$res_proj,$partfyn,$partf_approv_num,$femayn,$fema_proj_num,$mult_proj,$bond_fund,$state_appro,$reserve_proj,$design,$construction,$partfid,$cwmtf_fund,$num,$passSQL)	{	global $project_spo_number_array;		if(empty($projNum)){$projNum="0000";}			$map="";	foreach($project_spo_number_array as $index=>$array)		{		if($array['projNum']==$projNum)			{			$spo_number=$array['spo_number'];			$lat=$array['lat'];			$long=$array['long'];			$name=str_replace(" ","+",$array['fac_name']);			if(!empty($lat))				{				@$map.="(<a href='https://maps.google.com/maps?safe=off&q=$lat,$long+($name)&um=1&ie=UTF-8&hl=en&sa=N&z=18' target='_blank'>$spo_number</a>) ";				}			}		}		$linkProjPay="<a href='partf_payments.php?proj_num=$projNum'>P</a>";	$linkProjEdit="<a href='partf.php?projNum=$projNum&Submit=Find'>E</a>";	$linkCenter="<a href='partf_payments.php?center=$Center'>$Center</a>";	$park=strtoupper($park);	$projName=strtoupper($projName);	$projCat=strtoupper($projCat);	$Center=strtoupper($Center);		echo "	<tr>	<td>$projNum $linkProjPay $linkProjEdit</td>	<td align='center'>$park</td>	<td>$projName</td>	<td align='center'>$projCat</td>	<td>$linkCenter</td>	<td>$budgCode</td>	<td>D=$design, C=$construction, Status=$statusPer</td>";		if(!empty($map))		{		echo "<td>$map</td>";		}		echo "</tr>";	// <td>$parkName</td>	}?>