<?php$query7 = "truncate table cid_fund_balances";$result7 =mysqli_query($connection, $query7) or die ("Couldn't execute query 7.  $query7");if($showSQL=="1"){echo "<br><br>$query7";}//if($level>4){$showSQL=1;}$query8 = "INSERT  INTO cid_fund_balances( center, fundsin, fundsout, payments )SELECT fund_in, sum( amount ) ,  '',  ''FROM partf_fund_transwhere 1 and fund_in != ''GROUP  BY fund_in";$result8 =mysqli_query($connection, $query8) or die ("Couldn't execute query 8.  $query8");if($showSQL=="1"){echo "<br><br>$query8";}//echo "<br><br>$sql";//exit;$query9 = "INSERT  INTO cid_fund_balances( center, fundsin, fundsout, payments )SELECT fund_out,  '', sum( amount ) ,  ''FROM partf_fund_transwhere 1 and fund_out != ''GROUP  BY fund_out";$result9 =mysqli_query($connection, $query9) or die ("Couldn't execute query 9.  $query9");if($showSQL=="1"){echo "<br><br>$query9";}//echo "<br><br>$sql";//exit;$query10 = "INSERT  INTO cid_fund_balances( center, fundsin, fundsout, payments )SELECT center,  '',  '', sum( amount )FROM partf_paymentsGROUP  BY center";//echo "$sql";exit;$result10 =mysqli_query($connection, $query10) or die ("Couldn't execute query 10.  $query10");if($showSQL=="1"){echo "<br><br>$query10";}//echo "<br><br>$sql";//exit;$query11 = "SELECT cid_fund_balances.center, center.center_desc as 'center_description', sum( fundsin - fundsout )  AS  'funds_allocated', sum( payments )  AS  'payments', sum( fundsin - fundsout - payments )  AS  'balance'FROM cid_fund_balancesLEFT  JOIN center ON cid_fund_balances.center = center.centerWHERE 1GROUP  BY center";$result11 =mysqli_query($connection, $query11) or die ("Couldn't execute query 11.  $query11");$num11=mysqli_num_rows($result11);if($showSQL=="1"){echo "<br><br>$query11";} echo "<br>DPR PARTF BUDGETS LOOKUP BY CENTERS<hr>";echo "<table><tr><td>Centers:<font color='red'> $num11 </font></td></tr></table>";echo "<table border=1>";echo "<tr>";echo "<th align=left><font color=brown>Center</font></th>"; echo "<th align=left><font color=brown>Center Description</font></th>"; echo "<th align=left><font color=brown>Funds Allocated</font></th>"; echo "<th align=left><font color=brown>Payments</font></th>"; echo "<th align=left><font color=brown>Balance</font></th>"; echo "</tr>";while ($row11=mysqli_fetch_array($result11)){// The extract function automatically creates individual variables from the array $row//These individual variables are the names of the fields queried from MySQLextract($row11);$funds_allocated=number_format($funds_allocated,2);$payments=number_format($payments,2);$balance=number_format($balance,2);if($c==''){$t=" bgcolor='$table_bg2' ";$c=1;}else{$t='';$c='';}echo "<tr$t>";                                   // echo "<td><a href='reports_one_budget_group_summary_by_center.php?budget_group=$budget_group'>$budget_group</a></td>"; 		 echo "<td>$center</td>            <td>$center_description</td> 	           <td>$funds_allocated</td> 	           <td>$payments</td>            <td>$balance</td>  </tr>";          } echo "</table></html>";//echo "<br><br>$sql";//exit;?>