<?phpfunction updateFund($trans_type,$proj_out,$fund_out,$proj_in,$fund_in,$amount,$trans_date,$post_date,$comments,$posted,$fid,$passFundIn,$passFundOut,$passProjIn,$name_park_proj,$post_yn,$trans_num,$trans_source,$ncas_in,$ncas_out,$grant_rec_name,$grant_rec_vendor,$grant_PO,$grant_num,$bo_2_denr_req_date,$ttNew){if($post_date){$posted='y';}else{$posted='n';}if($ttNew){$trans_type=$ttNew;}$dateA=explode("/",$post_date);if(strlen($dateA[2])==4){$year=$dateA[2];}else{$year="20".$dateA[2];}$month=str_pad($dateA[0], 2, "0", STR_PAD_LEFT);$day=str_pad($dateA[1], 2, "0", STR_PAD_LEFT);$newdate=$year.$month.$day;if(checkdate($month,$day,$year)){$query = "UPDATE partf_fund_trans set trans_type='$trans_type',proj_out='$proj_out',fund_out='$fund_out',proj_in='$proj_in',fund_in='$fund_in',amount='$amount',trans_date='$trans_date',post_date='$post_date',comments='$comments',posted='$posted',post_yn='$post_yn',trans_num='$trans_num',trans_source='$trans_source',ncas_in='$ncas_in',ncas_out='$ncas_out',grant_rec_name='$grant_rec_name',grant_rec_vendor='$grant_rec_vendor',grant_PO='$grant_PO',grant_num='$grant_num',bo_2_denr_req_date='$bo_2_denr_req_date',datenew='$newdate' WHERE fid='$fid'";$result = mysqli_query($connection, $query) or die ("Couldn't execute query. $query");//exit;} else {echo "There was a problem. Click your browser's back button and check the Post Date.<br><br>$query";}}?>