r position number $beacon_num - $posTitle @ $parkcode";if(!$recToSup){echo "<tr><form><td align='right'>Before sending an email Recommendation to the District Superintendent or Hiring Manager's Supervisor submit the date:</td><td><input type='text' name='recToSup' value='$today' size='10'><input type='hidden' name='beacon_num' value='$beacon_num'><input type='submit' name='panel' value='Submit'></td></form></tr></table>";exit;}	else{echo "<tr><td align='right'>Email Recommendation to <a href='mailto:?subject=$subject'>District Superintendent or Hiring Manager's Supervisor</a>:</td><td>$recToSup</td></tr>";}if($level<2){exit;}if(!$supToSup){echo "<tr><form><td align='right'>Before sending an email Recommendation to the CHOP or Section Chief submit the date:</td><td><input type='text' name='supToSup' value='$today' size='10'><input type='hidden' name='beacon_num' value='$beacon_num'><input type='submit' name='panel' value='Submit'></td></form></tr></table>";exit;}	else{echo "<tr><form><td align='right'>Email Recommendation to <a href='mailto:?subject=$subject'>CHOP or Section Chief</a>:</td><td>$supToSup</td></tr>";}if($level<3){exit;}if(!$supToHR){echo "<tr><form><td align='right'>Before sending an email Recommendation to the District HR submit the date:</td><td><input type='text' name='supToHR' value='$today' size='10'><input type='hidden' name='beacon_num' value='$beacon_num'><input type='submit' name='panel' value='Submit'></td></form></tr>";}	else{echo "<tr><form><td align='right'>Email Recommendation to District HR <a href='mailto:carrie.paige@ncdenr.gov?subject=$subject'>(Carrie-NODI/WEDI)</a> <a href='mailto:teresa.mccall@ncdenr.gov?subject=$subject'>(Teresa-ARCH/EADI/SODI)</a>:</td><td>$supToHR</td></tr>";}echo "</table>";echo "</html>";?>