<?php// *************** EEID FUNCTIONS **************function findMe($fld,$passSQL){$findme=urlencode(" order by ");$pos=strpos($passSQL,$findme);if($pos>0){$flds = array("age","location","eeid");$removeFld = str_replace($flds, "", $passSQL);$passSQL=$removeFld.urlencode("$fld");}else{$passSQL=$passSQL.urlencode(" order by $fld");}return $passSQL;}function eeidHeader($passSQL){$link="<a href='display_sort.php?park=$park'>$park</a>";echo "<table border='1' cellpadding='3'><tr><th>EEID</th><th>District</th><th>County</th><th>Park</th>";$fld="age";$a=findMe($fld,$passSQL);echo "<th><a href='display_sort.php?passSQL=$a'>Audience</th>";echo "<th>Category</th>";$fld="location";$b=findMe($fld,$passSQL);echo "<th><a href='display_sort.php?passSQL=$b'>Location</th>";echo "<th>Prog. Date</th><th>Presenter</th><th>Prog. Title</th><th>Prog. Given</th><th>Attendance</th><th>Comment</th><th>Resources</th></tr>";}function itemShow($age,$category,$county,$dateprogram,$dist,$location,$park,$presenter,$comments,$resources,$timegiven,$progtitle,$attend,$username,$datecreate,$datemod,$mark,$eeid,$passSQL){global $attendTot,$timegivenTot,$a;$attendTot=$attendTot+$attend;$timegivenTot=$timegivenTot+$timegiven;$timegiven=number_format($timegiven,0);$attend=number_format($attend,0);$link="<a href='display_sort.php?park=$park'>$park</a>";$linkID="<a href='display_sort.php?passSQL=$passSQL&eeidFld=$eeid'>$eeid</a>";echo "<tr><td align='center'>$linkID</td><td align='center'>$dist</td><td align='left'>$county</td><td align='left'>$link</td><td align='left'>$age</td><td align='center'>$category</td><td align='center'>$location</td><td align='center'>$dateprogram</td><td align='center'>$presenter</td><td align='center'>$progtitle</td><td align='right'>$timegiven</td><td align='right'>$attend</td><td>$comments</td><td>$resources</td></tr>";}?>