<?php         function get_date_from_week($year, $week){global $year, $week_one_start, $week;	$first_day = strtotime($year."-01-01");	$is_monday = date("w", $first_day) == 1;	$is_weekone = strftime("%V", $first_day) == 1;	if($is_weekone)	{		$week_one_start = $is_monday ? $first_day : strtotime("last monday", $first_day);	}	else	{		$week_one_start = strtotime("next monday", $first_day);	}	return $week_one_start+(3600*24*7*($week-1)-3600*24);}                   function getWeekNumber($n){global $year, $week, $weekEnd, $n;//$year = date(Y);//$week = $n;$weekEnd = date("M-d-Y", get_date_from_week($year, $week));echo "Week $n ends $weekEnd";}echo "<select name=\"weekTest\">";$testWeek = $week-1;for ($n=0;$n<=53;$n++){$weekList=getWeekNumber($n);if ($n == $testWeek){echo "<option value='' selected>$weekList\n";} ELSE {echo "<option value=''>$weekList\n";}}echo "</select>";$year="2006";$week="15";$thisWeek = date("M-d-Y", get_date_from_week($year,$week));echo "<br><br>$week_one_start $thisWeek $weekFind";?>