g='5'>";while($row=mysqli_fetch_array($result))	{	extract($row);	if(@$edit){$orderx="<a href='tips.php?orderx=$orderx'>$i</a>";}	$i++;	$tip=nl2br($tip);	echo "<tr><td align='left'><font size='+1'>$orderx - $tip</font></td></tr>";	}echo "</table></div>";?>