<?phpini_set('display_errors',1);date_default_timezone_set('America/New_York');if(empty($_FILES['file_upload'])){exit;}// ********** ACTION// includes deletion of previous file since a timestamp is used to get around browser cacheing $num=count($_FILES['file_upload']['tmp_name']);// echo "<pre>"; print_r($_FILES); echo "</pre>";  exit;for($i=0;$i<$num;$i++)	{	$temp_name=$_FILES['file_upload']['tmp_name'][$i];	if($temp_name==""){continue;}	if(!is_uploaded_file($_FILES['file_upload']['tmp_name'][$i])){exit;}	$original_file_name = $_FILES['file_upload']['name'][$i];	$exp=explode(".",$original_file_name);	$ext=array_pop($exp);	// if(!empty($existing_map))	// 	{	// 	unlink($existing_map);	// 	}	$uploaddir = "uploads"; // make sure www has r/w permissions on this folder	if (!file_exists($uploaddir)) {mkdir ($uploaddir, 0777);}	$sub_folder=$uploaddir."/".date("Y");	if (!file_exists($sub_folder)) {mkdir ($sub_folder, 0777);}	$ts=time();	$file_name=$contact_type."_".$record_id."_".$ts.".".$ext;	$uploadfile = $sub_folder."/".$file_name;	move_uploaded_file($temp_name,$uploadfile);// create file on server	chmod($uploadfile,0777);	$sql="INSERT INTO uploads set record_id='$record_id', file_name='$original_file_name', link='$uploadfile'"; 	$result = mysqli_query($connection,$sql) or die("$sql Error 1#". mysqli_errno($connection) . ": " . mysqli_error($connection));		include("thumbnail.php");}?>