<?php$database="state_lakes";include("../../include/connectROOT.inc");// database connection parameters$db = mysql_select_db($database,$connection)       or die ("Couldn't select database $database");          //echo "<pre>"; print_r($_POST); echo "</pre>";  //exit;       $display_fields="*";foreach($_POST['id'] AS $k=>$v)	{		$clause.="id='$v' OR ";	}$clause=rtrim($clause," OR ");$sql = "SELECT $display_fields FROM contacts as t1 WHERE  $clause";  //echo "$sql";exit;$result = mysql_query($sql) or die ("Couldn't execute query. $sql c=$connection");while($row=mysql_fetch_assoc($result)){$ARRAY[]=$row;}echo "<form method='POST' action='edit.php'><table cellpadding='5' border='1'>";	echo "<tr><th></th>";	foreach($ARRAY[0] as $k=>$v){					if($k=="id"){continue;}					echo "<th>$k</th>";					}	echo "</tr>";		foreach($ARRAY as $num=>$row){			echo " <tr valign='top'><td align='right'>$num</td>";						foreach($row as $fld=>$value){					$id=$row['id'];					$fld_array=$num."[$fld]";					if($fld=="id")						{					$fld_array=$num."[id]";					echo "<input type='hidden' name='$fld_array' value='$id'>";continue;						}										if($fld=="justification"){					$toggle="<div id=\"fieldName\">   ... <a onclick=\"toggleDisplay('fieldDetails[$num]');\" href=\"javascript:void('')\"> details &#177</a> <font size='-1'>$related</font></div>					<div id=\"fieldDetails[$id]\" style=\"display: none\"><br><textarea name='$fld_array' cols='55' rows='15'>$value</textarea></div>";					echo "<td>$toggle</td>";					}					else{						if($fld=="beacon_title"){							$img=" <a href='position_justification.php?del=$id' onClick='return confirmLink()'><img src='../../button_drop.png'></a>";						} else{$img="";}						echo "<td><input type='text' name='$fld_array' value=\"$value\" size='17'>$img</td>";}				}			echo "</tr>";			}				echo "<tr><td colspan='19' align='center'>	<input type='submit' name='submit' value='Update'>	</td></tr>";	echo "</table></form></html>";	?>