<?php// echo "<pre>"; print_r($_POST); echo "</pre>";  exit;$database="dpr_system";include("../../include/iConnect.inc"); // database connection parametersmysqli_select_db($connection,$database)       or die ("Couldn't select database");$readonly=array("id","year","unit_code","unit","unit_type","classification","sub_classification");// $drop_down=array("unit_type");$radio_array=array("land_area_only","water_area_only","undeveloped_unit","operated_units","has_easements_along_river","conservation_fund_donation_estimate","includes_scuppernong_river_acreage","unregistered_survey_estimate","gis_estimate","unsurveyed_estimate","includes_easement_acres");$yes_no=array("yes","no");if($_POST['submit_form']=="Add")	{	$skip_add=array("id","submit_form");	$temp=array();// 	echo "18<pre>"; print_r($_POST); echo "</pre>";  //exit;	foreach($_POST as $fld=>$value)		{		if(in_array($fld, $skip_add)){continue;}		$temp[]="$fld='$value'";		}	$clause="set ".implode(",", $temp); //echo "$clause";	$sql="INSERT INTO acreage $clause";	echo "$sql"; exit;	// 	$result = @mysqli_QUERY($connection,$sql) or die(mysqli_error($connection));	}if($_POST['submit']=="Add")	{	$sql="SELECT * from acreage where id='$id'";	$result = @mysqli_QUERY($connection,$sql) or die(mysqli_error($connection));	while($row=mysqli_fetch_assoc($result))		{		$ARRAY[]=$row;		}// 		echo "<pre>"; print_r($ARRAY); echo "</pre>";  //exit;	$skip=array("id");	include("menu.php");$unit_code=$ARRAY[0]['unit_code'];echo "<form action=\"acreage.php\" method=\"post\" id=\"form1\"><input type=\"hidden\" name=\"unit_code\" value='$unit_code'></form>";	echo "<form method='POST' action='add_record.php'>	<table id='acreage_add' class=\"center\"><tr><th colspan='3' >Add Record for ".$ARRAY[0]['unit']."</th>	</tr>	<tr><th>Field</th><th>Value</th>	<th><button  type='submit' form='form1' value='Submit'>Return</button></th>";	echo "</tr>";	foreach($ARRAY AS $index=>$array)		{		foreach($array as $fld=>$value)			{			if(in_array($fld, $skip)){continue;}			$ro="";			$RO="";			if(in_array($fld,$readonly))				{				$ro="readonly";				$RO="<font size='-2'>readonly</font>";				}			if(in_array($fld, $radio_array))				{				$dd_array=$yes_no;				echo "<tr><td>$fld</td>";				echo "<td>";				foreach($dd_array as $k=>$v)					{					if($value==$v){$ck="checked";}else{$ck="";}					echo "$v<input type='radio' name='$fld' value=\"$v\" $ck>";					}				echo "</td>";				continue;				}			echo "<tr><td>$fld $RO</td>			<td>			<input type='text' name='$fld' value=\"$value\" $ro>			</td></tr>";			}		}	echo "<tr><th colspan='2'>	<input type='hidden' name='id' value=\"$id\">	<input type='submit' name='submit_form' value=\"Add\">	</th></tr>";	echo "</table></html>";	}?>